    public static String convert(String s) {
        StringBuilder sb = new StringBuilder(s.length());
        for(int i = 0; i < s.length(); ++i) {
            char c = s.charAt(i);
            int offset = trad.indexOf(c);
            if(-1 != offset) c = simp.charAt(offset);
            sb.append(c);
        }
        return sb.toString();
    }

    public static void main(String[] args) {
        String trad = "坣灣是位於亞洲東部、太平洋西北側的島嶼,面積約3.6萬平方公裏,另有寶島、鯤島、福爾摩沙等別稱。"
        		+ "臺灣是由歐亞大陸板塊、沖繩板塊和菲律賓海板塊擠壓而隆起的島嶼";
        String simp = Trad2Simp.convert(trad);
        System.out.println(trad);
        //System.out.println(HzPyUtil.getFirstLetter(trad));
        System.out.println(simp);
        //System.out.println(HzPyUtil.getFirstLetter(simp));
        System.out.println(CnToSpell.getFirstSpell(simp));
        System.out.println(CnToSpell.getFullSpell(simp));
    }
    
